export const setDataShopping = (data) => {
  const value = JSON.stringify(data);
  const arr = [];
  arr.push(value);
  localStorage.setItem("shopping", arr);
};

export const getDataShopping = () => {
  return JSON.parse(localStorage.getItem("shopping"));
};
